import React, { useState } from "react";
import { useNavigate } from "react-router-dom";


const movies = [
    { id: 1, title: "The Holdovers", image: "data:image/jpeg;base64," },
    { id: 2, title: "Oppenheimer", image: "data:image/jpeg;base64," },
    { id: 3, title: "Barbie", image: "https://resizing.flixster.com/-XZAfHZM39UwaGJIFWKAE8fS0ak=/v3/t/assets/p13472534_p_v8_am.jpg" },
    { id: 4, title: "Gran Turismo", image: "https://images.moviesanywhere.com/2aae664b5ba55db2ed58e7ea14ed79ba/da201cf3-5fc6-4bee-b9d1-5a8c6e263244.jpg" },
];

const Home = () => {
    const [searchTerm, setSearchTerm] = useState("");
    const navigate = useNavigate();

    const filteredMovies = movies.filter((movie) =>
        movie.title.toLowerCase().includes(searchTerm.toLowerCase())
    );

    

    return (
        <div style={{ padding: "20px", backgroundColor: "#0f0f0f", minHeight: "100vh" }}>
            <input
                type="text"
                placeholder="Enter the name or genre of the movie"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                style={{
                    width: "50%",
                    padding: "10px",
                    marginBottom: "20px",
                    fontSize: "16px",
                    borderRadius: "8px",
                }}
            />
            <div style={{ display: "flex", gap: "20px", flexWrap: "wrap" }}>
                {filteredMovies.map((movie) => (
                    <div
                        key={movie.id}
                        onClick={() => navigate(`/section/${movie.id}`)}
                        style={{ cursor: "pointer", color: "white", width: "200px" }}
                    >
                        <img
                            src={movie.image}
                            alt={movie.title}
                            style={{ width: "100%", borderRadius: "12px" }}
                        />
                        <p style={{ textAlign: "center", marginTop: "10px" }}>{movie.title}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
